此文仅做杂谈，无技术含量

```
#include <iostream>
#include <algorithm>
#include <string>
using namespace std;

void myReverse(const string& str) {
    char* c_str = (char*)str.c_str();
    char* p, * q;
    p = c_str;
    q = c_str + str.size() - 1;
    while (p < q) {
        char temp_c = *p;
        *p = *q;
        *q = temp_c;
        p++;
        q--;
    }
}

int main() {
    string str = "123456789";
    myReverse(str);    // 反转方式1
    cout << str << endl;
    reverse(str.begin() + 2, str.end() - 2);    // 反转方式2
    cout << str << endl;
    cout << string(str.rbegin(), str.rend());    // 反转方式3
    return 0;
}
```

